function servers_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('servers');
	if(!$arrPerm['insert']) return false;

	$data = [
		'ip_address' => Request::val('ip_address', ''),
		'client' => Request::lookup('client', ''),
		'hosted_at' => Request::lookup('hosted_at', ''),
		'data_center' => Request::lookup('data_center', ''),
		'hostname' => Request::val('hostname', ''),
		'ram' => Request::val('ram', ''),
		'storage' => Request::val('storage', ''),
		'os' => Request::lookup('os', ''),
		'comments' => Request::val('comments', ''),
	];


	// hook: servers_before_insert
	if(function_exists('servers_before_insert')) {
		$args = [];
		if(!servers_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('servers', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('servers', $recID, calculated_fields()['servers']);

	// hook: servers_after_insert
	if(function_exists('servers_after_insert')) {
		$res = sql("SELECT * FROM `servers` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!servers_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('servers', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) servers_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

