function servers_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('servers', $selected_id, 'edit')) return false;

	$data = [
		'ip_address' => Request::val('ip_address', ''),
		'client' => Request::lookup('client', ''),
		'hosted_at' => Request::lookup('hosted_at', ''),
		'data_center' => Request::lookup('data_center', ''),
		'hostname' => Request::val('hostname', ''),
		'ram' => Request::val('ram', ''),
		'storage' => Request::val('storage', ''),
		'os' => Request::lookup('os', ''),
		'comments' => Request::val('comments', ''),
	];

	// get existing values
	$old_data = getRecord('servers', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: servers_before_update
	if(function_exists('servers_before_update')) {
		$args = ['old_data' => $old_data];
		if(!servers_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'servers', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="servers_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('servers', $data['selectedID'], calculated_fields()['servers']);

	// hook: servers_after_update
	if(function_exists('servers_after_update')) {
		$res = sql("SELECT * FROM `servers` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!servers_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='servers' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

